	<?php 
	     
		// php code goes here
	if (isset($_POST["submit"])){
		
		$addressFrom = $_POST['tags'];
		$addressTo = $_POST['tags1'];
		
		$formattedAddrFrom = str_replace(' ','%20',$addressFrom);
		$formattedAddrTo = str_replace(' ','%20',$addressTo);
    
		//Send request and receive json data
		$geocodeFrom = file_get_contents('https://maps.google.com/maps/api/geocode/json?address='.$formattedAddrFrom.'&key=AIzaSyBNV9P7AtJrPqIjZI1JCB3u0BOWXOGcTN0');
		
		$outputFrom = json_decode($geocodeFrom);
		
		$geocodeTo = file_get_contents('https://maps.google.com/maps/api/geocode/json?address='.$formattedAddrTo.'&key=AIzaSyBNV9P7AtJrPqIjZI1JCB3u0BOWXOGcTN0');
		$outputTo = json_decode($geocodeTo);
    
		//Get latitude and longitude from geo data
		$latitudeFrom = $outputFrom->results[0]->geometry->location->lat;
		$longitudeFrom = $outputFrom->results[0]->geometry->location->lng;
		$latitudeTo = $outputTo->results[0]->geometry->location->lat;
		$longitudeTo = $outputTo->results[0]->geometry->location->lng;
		
		$theta = $longitudeFrom - $longitudeTo;
		$dist = sin(deg2rad($latitudeFrom)) * sin(deg2rad($latitudeTo)) +  cos(deg2rad($latitudeFrom)) * cos(deg2rad($latitudeTo)) * cos(deg2rad($theta));
		$dist = acos($dist);
		$dist = rad2deg($dist);
		$miles = round($dist * 60, 2);
		
		echo '<div id="output">The distance between <strong>'.$addressFrom.'</strong> and <strong>'.$addressTo.'</strong> is <strong>'.$miles.'</strong> nautical miles. </div>';
	
	}	
		?>
		

