      // This creates a 2-pixel-wide blue polyline showing the path between 
      // the two points.
      function initMap() {
        var map = new google.maps.Map(document.getElementById('map'), {
          zoom: 4,
          center: {lat: 40, lng: -95},
          mapTypeId: google.maps.MapTypeId.TERRAIN
        });
		
		var lineSymbol = {
		path: google.maps.SymbolPath.FORWARD_CLOSED_ARROW
		};
		
		var lat1 = <?php echo json_encode($latitudeFrom); ?>;
		var lng1 = <?php echo json_encode($longitudeFrom); ?>;
		var lat2 = <?php echo json_encode($latitudeTo); ?>;
		var lng2 = <?php echo json_encode($longitudeTo); ?>;
		
        var flightPlanCoordinates = [
          {lat: lat1, lng: lng1},
          
          {lat: lat2, lng: lng2}
        ];
		
        var flightPath = new google.maps.Polyline({
          path: flightPlanCoordinates,
          geodesic: true,
          strokeColor: '#00B3FD',
          strokeOpacity: 1.0,
          strokeWeight: 2,
		  icons: [{
			icon: lineSymbol,
			offset: '100%'
			}]
        });
			
        flightPath.setMap(map);
      }
    </script>
